                    <div class="form-group my-2 d-flex align-items-center">
                      <label class="font-size-16 me-2" for="cancelation"> Yes </label>
                      <input required
                        id="cancelation"
                        type="radio"
                        name="option"
                        value="option1"
                        onclick="toggleField('option2')" />
                    </div>
                  </div>
                  <div class="col-md-9">
                    <div class="form-group my-2 d-flex align-items-center">
                      <label class="font-size-16 me-2" for="approval">  No </label>
                      <input required
                        id="approval"
                        class="p-2"
                        type="radio"
                        name="option"
                        value="option1"
                        onclick="toggleField('option1')" />
                    </div>
                  </div>
                  <div
                    id="additionalField"
                    class="col-md-6 additional-field">
                    <div class="form-group">
                      <label>Please Mention Their Names</label>
                      <input required
                        class="form-control"
                        type="text"
                        id="additionalInput"
                        name="additionalInput"
                        placeholder="Please Mention Their Names" />
                    </div>
                  </div>

                  <div class="col-md-12 text-center">
                    <div class="form-group mb-0">
                      <button
                        class="btn btn-theme btn-block"
                        type="submit">
                        Submit My request
                      </button>
                    </div>
                  </div>
                </div>
