<section class="py-5" data-bg-color="#eaeded" style="background-color: rgb(234, 237, 237);">

<div class="container">
<div class="row">
  <div class="col-lg-12">
    <div class="contact-form">
      <div class="section-title text-center" data-aos="fade-up" data-aos-duration="1100">
        <h2 class="title">Request For <span>Med Right Medical Approval</span></h2>
        <h4>Submit Your Request For A New Approval Or For A Previous Approval Request Cancellation.</h4>
      </div>
      <form class="contact-form-wrapper" id="requestMedicalApproval" action="" method="" data-aos="fade-up" data-aos-duration="1100">
        <div class="row">
          <div class="col-md-6">
            <div class="form-group">
              <input id="approval" class="p-2" checked type="radio" name="option" value="option1" onclick="toggleField('option1')">
              <label for="approval">
                Approval Request
              </label>
            </div>
          </div>
          <div class="col-md-6">
            <div class="form-group">
              <input id="cancelation" type="radio" name="option" value="option1" onclick="toggleField('option2')">
              <label for="cancelation">
                Cancellation Request
              </label>
            </div>
          </div>
          <div class="col-md-12">
          <div id="additionalField" class="additional-field form-group">
            <input class="form-control" type="text" id="additionalInput" name="additionalInput" placeholder="Approval Form Number">
          </div>
          </div>

          <div class="col-md-4">
            <div class="form-group">
              <input class="form-control" type="text" name="con_name" placeholder="Your Full Name">
            </div>
          </div>
          <div class="col-md-4">
            <div class="form-group">
              <input class="form-control" type="text" name="con_company" placeholder="Company Name">
            </div>
          </div>
          <div class="col-md-4">
            <div class="form-group">
              <input class="form-control" type="text" name="con_id" placeholder="Membership Id">
            </div>
          </div>
          <div class="col-md-6">
            <div class="form-group">
              <input class="form-control" type="email" name="con_email" placeholder="Email Address">
            </div>
          </div>
          <div class="col-md-6">
            <div class="form-group">
              <input class="form-control" type="text" name="con_suggested" placeholder="Suggested Provider Name">
            </div>
          </div>

          <div class="col-md-12">
            <select class="form-select form-select-lg" name="selectCat" id="selectCat" aria-label="Default select example">
              <option value="" selected>Open this select menu</option>
              <option value="in-Patient">In-Patient</option>
              <option value="medication">Medication</option>
              <option value="scan-and-imaging">Scan And Imaging</option>
              <option value="labs">Labs</option>
              <option value="physiotherapy">Physiotherapy</option>
              <option value="dental">dental</option>
              <option value="others">Others</option>
            </select>
          </div>
          <div class="col-md-12 mt-4">
            <div class="form-group">
              <input class="form-control" type="file" name="con_file">
            </div>
          </div>
          <div class="col-md-12 mt-4">
            <div class="form-group mb-0">
              <textarea name="con_message" rows="5" placeholder="Additional info can support your request"></textarea>
            </div>
          </div>
          <div class="col-md-12 text-center">
            <div class="form-group mb-0">
              <button class="btn btn-theme btn-block" type="submit">Submit My request</button>
            </div>
          </div>
        </div>
      </form>
    </div>
    <!-- Message Notification -->
    <div class="form-message"></div>
  </div>
</div>
</div>

</section>

